import $ from 'jquery';

export const apiUrl = 'http://localhost:8000/api/v1';

/**
 * @desc Get categories
 * @returns {Promise<any>}
 */
export const getCategories = () => {
    return new Promise((resolve, reject) => {
        $.ajax({
            url: `${apiUrl}/categories`,
            type: 'GET',
            success: (result) => resolve(result),
            error: (error) => reject(error)
        });
    });
};
